mapper.select:
SELECT
    COUNT(*) AS VALUE, ALARM_LEVEL AS NAME
FROM
    ALARM_INFORMATION

    ALARM_STATE = 1

        AND RELEASE_TIME > #{begin}


        AND RELEASE_TIME < #{end}


        AND ALARM_TYPE_LV2 IN (SELECT DISTINCT LV_2 FROM ALARM_INFO_CODE WHERE LV_2_NAME LIKE #{alarm_condition})


        AND ALARM_TYPE_LV1 = #{type_v1}


        AND ALARM_TYPE_LV2 = #{type_v2}


GROUP BY
    ALARM_LEVEL

SELECT t2.LV_1 AS NAME, NVL(t1.VALUE, 0) AS DATA, t2.LV_1_NAME AS VALUE FROM ( SELECT COUNT(*) AS VALUE, ALARM_TYPE_LV1 AS NAME FROM ALARM_INFORMATION
ALARM_STATE = 1
AND ALARM_LEVEL in (${level})
AND ALARM_TYPE_LV2 IN (SELECT DISTINCT LV_2 FROM ALARM_INFO_CODE WHERE LV_2_NAME LIKE #{alarm_condition})
AND RELEASE_TIME > #{begin}
AND RELEASE_TIME < #{end}
GROUP BY ALARM_TYPE_LV1 ) t1 RIGHT JOIN ( SELECT DISTINCT LV_1, LV_1_NAME FROM ALARM_INFO_CODE
LV_2_NAME LIKE #{alarm_condition}
) t2 ON t1.NAME = t2.LV_1

SELECT t2.LV_2 AS CODE, NVL(t1.VALUE,0) AS COUNT, t2.LV_2_NAME AS NAME FROM ( SELECT COUNT(*) AS VALUE, ALARM_TYPE_LV2 AS NAME FROM ALARM_INFORMATION
ALARM_STATE = 1
AND ALARM_LEVEL in (${level})
ALARM_TYPE_LV1 = #{type_v1}
AND RELEASE_TIME > #{begin}
AND RELEASE_TIME < #{end}
GROUP BY ALARM_TYPE_LV2 ) t1 RIGHT JOIN ( SELECT DISTINCT LV_2, LV_2_NAME FROM ALARM_INFO_CODE
LV_1 = #{type_v1}
AND LV_2_NAME LIKE #{alarm_condition}
) t2 ON t1.NAME = t2.LV_2
