mapper.select:
SELECT OBJECT_ID, YJJC_CREATE_TIME, YJJC_UPDATE_TIME, NAME, USER_NAME, MAIN_ID, STATUS
FROM DISTRICT_CLASS


        and NAME = #{NAME}


        and MAIN_ID = #{MAIN_ID}

SELECT OBJECT_ID, YJJC_CREATE_TIME, YJJC_UPDATE_TIME, NAME, USER_NAME, MAIN_ID, STATUS
FROM DISTRICT_CLASS


        and OBJECT_ID = #{OBJECT_ID}


        and NAME like concat('%',concat(#{NAME},'%'))


        and USER_NAME like concat('%',concat(#{USER_NAME},'%'))


        and MAIN_ID = #{MAIN_ID}


        and STATUS = #{STATUS}

SELECT a.OBJECT_ID, a.YJJC_CREATE_TIME, a.YJJC_UPDATE_TIME, a.NAME, a.USER_NAME, a.MAIN_ID, a.STATUS,b.groupname FROM (SELECT OBJECT_ID, YJJC_CREATE_TIME, YJJC_UPDATE_TIME, NAME, USER_NAME, MAIN_ID, STATUS FROM DISTRICT_CLASS WHERE MAIN_ID != '0' AND STATUS = '1')a, (SELECT OBJECT_ID,NAME groupname,MAIN_ID FROM DISTRICT_CLASS)b
b.OBJECT_ID = a.MAIN_ID
and a.OBJECT_ID = #{OBJECT_ID}
and a.NAME like concat('%',concat(#{NAME},'%'))
and a.USER_NAME like concat('%',concat(#{USER_NAME},'%'))
and a.MAIN_ID = #{MAIN_ID}

SELECT a.OBJECT_ID, a.YJJC_CREATE_TIME, a.YJJC_UPDATE_TIME, a.NAME, a.USER_NAME, a.MAIN_ID, a.STATUS,b.groupname FROM (SELECT OBJECT_ID, YJJC_CREATE_TIME, YJJC_UPDATE_TIME, NAME, USER_NAME, MAIN_ID, STATUS FROM DISTRICT_CLASS WHERE MAIN_ID != '0' AND STATUS = '1')a, (SELECT OBJECT_ID,NAME groupname,MAIN_ID FROM DISTRICT_CLASS)b, (SELECT OBJECT_ID,MAIN_ID FROM DISTRICT_CLASS WHERE MAIN_ID = '0' AND STATUS = '0')c
b.OBJECT_ID = a.MAIN_ID AND c.OBJECT_ID != a.MAIN_ID
and a.OBJECT_ID = #{OBJECT_ID}
and a.NAME like concat('%',concat(#{NAME},'%'))
and a.USER_NAME like concat('%',concat(#{USER_NAME},'%'))
and a.MAIN_ID = #{MAIN_ID}
