mapper.select:
SELECT SANXIAO_PLACE_NUM, DANGER_SLOPE,ROAD_DISEASE_NUM,IOT_FIRE_NUM,RISK_POINT_NUM FROM ( SELECT count(*) SANXIAO_PLACE_NUM FROM BLK_SANXIAO_PLACE WHERE STATUS = '0'
AND STREET_CODE = #{streetCode}
), ( SELECT count(ID_NUM) DANGER_SLOPE FROM DANGER_SLOPE
AND STREET_CODE = #{streetCode}
), ( SELECT count(*) ROAD_DISEASE_NUM FROM TB_ROAD_DISEASE
AND STREET_CODE = #{streetCode}
), ( SELECT COUNT(*) IOT_FIRE_NUM FROM IOT_DEVICE where 1=1 and ( device_type = '0022' or device_type ='0003' or device_type = '0024' or device_type ='0025' )
AND STREET_CODE = #{streetCode}
), ( SELECT count(*) RISK_POINT_NUM FROM RISK_POINT
AND STREET_CODE = #{streetCode}
)

SELECT STREET_NAME AS NAME, COUNT(OBJECT_ID) AS VALUE FROM BLK_SANXIAO_PLACE WHERE HAS_TROUBLE = 1 AND STREET_NAME IS NOT NULL
AND STREET_CODE = #{streetCode}
GROUP BY STREET_NAME

SELECT STREET_NAME as NAME, count(STREET_NAME) as VALUE FROM ( SELECT c.STREET_NAME FROM DANGER_SLOPE a, ( SELECT b.STREET_CODE, b.STREET_NAME FROM COMMUNITY_CODE b GROUP BY b.STREET_NAME, b.STREET_CODE ) c WHERE a.STREET_CODE = c.STREET_CODE
AND a.STREET_CODE = #{streetCode} AND c.STREET_CODE = #{streetCode}
) GROUP BY STREET_NAME

SELECT t1.id,t1.place_type,t1.name,t1.eventContent,t1.eventTime,t1.state,t1.SYSTEMID,t2.place_name FROM (
SELECT a.id,a.status, a.place_type, a.name, a.address, b.event_content eventContent, b.event_time eventTime, b.state, b.SYSTEMID FROM ( SELECT id, status, place_type, name, address FROM BLK_SANXIAO_PLACE
AND STREET_CODE = #{streetCode}
AND PLACE_TYPE = #{placeType}
) a JOIN ( SELECT event_content,event_time,state,SYSTEMID,address,event_name FROM BLK_CHENGGUAN_EVENT WHERE event_source = 6 AND (state = 0 OR state = 1) AND isdelete = '0' ) b ON a.address = b.address AND a.name = b.event_name
ORDER BY eventTime DESC,NAME ) t1, ( SELECT CODE_IN_CHINESE PLACE_NAME, CODE FROM DS_VALUE_DICT WHERE TABLE_NAME = 'BLK_SANXIAO_PLACE' AND COLUMN_NAME = 'PLACE_TYPE' ) t2
t1.PLACE_TYPE = t2.CODE
and t1.PLACE_TYPE = #{placeType}

SELECT CODE AS type, t2.PLACE_NAME AS name, COUNT(ID) as value FROM( SELECT t1.*, t2.filepath FROM( SELECT a.id, a.place_type, a.name, b.event_content eventContent, b.event_time eventTime, b.state, b.SYSTEMID FROM BLK_SANXIAO_PLACE a, BLK_CHENGGUAN_EVENT b WHERE a.name = b.event_name AND a.address = b.address AND b.event_source = 6 AND (b.state = 0 OR b.state = 1) AND b.isdelete = '0'
AND a.STREET_CODE = #{streetCode}
AND b.event_time > to_date(#{startTime},'yyyy-MM-dd')
AND b.event_time <= to_date(#{endTime},'yyyy-MM-dd')
) t1 LEFT JOIN( SELECT wm_concat(filepath) AS filepath, SYSTEM_ID FROM BLK_CG_EVT_ATTS GROUP BY SYSTEM_ID ) t2 ON t1.SYSTEMID = t2.SYSTEM_ID ) t1 JOIN(SELECT CODE_IN_CHINESE place_name, code FROM DS_VALUE_DICT WHERE TABLE_NAME = 'BLK_SANXIAO_PLACE' AND COLUMN_NAME = 'PLACE_TYPE' ) t2 ON t1.place_type = t2.code GROUP BY t2.code, t2.PLACE_NAME

SELECT wm_concat(filepath) as VALUE, SYSTEM_ID AS NAME FROM BLK_CG_EVT_ATTS WHERE SYSTEM_ID IN
#{systemId}
GROUP BY SYSTEM_ID

SELECT t1.LV_2 as type, t1.LV_2_NAME as name, COUNT(t2.OBJECT_ID) as value FROM ( SELECT LV_2, LV_2_NAME FROM ALARM_INFO_CODE WHERE LV_1 = '002' OR LV_1 = '003' OR LV_1 = '004' OR LV_1 = '011' ) t1 JOIN ( SELECT * FROM ALARM_INFORMATION WHERE SEND_STATE = '1' AND ALARM_STATE = '1'
AND STREET_CODE = #{streetCode}
) t2 ON t1.LV_2 = t2.ALARM_TYPE_LV2 GROUP BY t1.LV_2, t1.LV_2_NAME HAVING COUNT(t2.OBJECT_ID) > 0

SELECT t2.OBJECT_ID as OBJECT_ID, t1.LV_2_NAME as LV_2_NAME, t2.ALARM_TYPE_LV2 as ALARM_TYPE_LV2, t2.ADDRESS as ADDRESS, t2.CONTENTS as CONTENTS, t2.ALARM_OBJECT as ALARM_OBJECT, t2.RELEASE_TIME as RELEASE_TIME FROM ( SELECT LV_2, LV_2_NAME FROM ALARM_INFO_CODE
LV_2 = #{type}
) t1 JOIN ( SELECT * FROM ALARM_INFORMATION WHERE SEND_STATE = '1' AND ALARM_STATE = '1'
AND ALARM_TYPE_LV2 = #{type}
AND STREET_CODE = #{streetCode}
) t2 ON t1.LV_2 = t2.ALARM_TYPE_LV2 WHERE t2.OBJECT_ID is not null
