mapper.select:
select  count(*)
from BLK_SANXIAO_PLACE where 1=1

    and PLACE_TYPE = #{PLACE_TYPE}


    and STREET = #{STREET}


    and to_char(CREATETIME,'yyyy-MM-dd HH24:mi:ss') like concat(#{CREATETIME},'%')

select ADDRESS,CREATETIME,QUCODE,JDCODE,SQCODE, STREET,STATUS,UUID,SQDM,UPDATE_TIME,NAME,AREA,OPERATOR_NAME,OPERATOR_TEL,OWNER_NAME,OWNER_TEL,BUSINESS_AREA, BUSINESS_TYPE,PLACE_TYPE,IS_BUSINESS_CARD,BUSINESS_CARD_NO,APPROVED_TYPE,GRID,GRID_NAME,LAST_CHECK_TIME, TIMES,ISADD,PHOTO,ISCHECK,ISDELETE,ISTROUBLE,CREATOR,CREATORID,LOGOUTER,LOGOUTTIME,STREETID,AUDIT_TIME, AUDIT_USER,ZX_AUDIT_TIME,ZX_AUDIT_USER,LOGOUTERID,REMARK,SQ,SQID,OWNER_CARD_ID,OPERATOR_CARD_ID,SHENG,SHENGDM, SHI,SHIDM,QU,QUDM,DL,DLDM,XQ,XQDM,LD,LDDM,FW,FWDM,CJSJ,GXSJ,SJLYFS,SJLY,DYWGCODE,CODE,ADDRCODE,ADDRDESC,BZDZ, LAT,LON,UPDATE_NAME,UPDATE_ID,CHECKSTATE,ISDOUBLECHECK,DOUBLECHECK_TIME,ISCHANGE,IS_UP,YHCOUNT,INIT_TIME, JD84,WD84,REGION_CODE,STREET_CODE,COMMUNITY_CODE,OBJECT_ID,YJJC_CREATE_TIME,YJJC_UPDATE_TIME,ID,HAS_TROUBLE from BLK_SANXIAO_PLACE where 1=1
and ID = #{ID}
and PLACE_TYPE = #{PLACE_TYPE}
and STREET_CODE = #{STREET_CODE}
