mapper.select:
select CREATETIME,CREATOR,DATA_STATUS,ID,IS_REVISED,MN,MONITOR_FACTOR_CODE, MONITOR_TIME,MONITOR_VALUE,REVISED_MONITOR_VALUE,SITE_CODE,STANDARD_VALUE,UPDATER,UPDATETIME, UPDATE_DATE,YXY_UPDATEDTIME,ADQ_UPDATE_TIME,REGION_CODE,STREET_CODE,COMMUNITY_CODE, JD84,WD84,ADDRESS,STREET_NAME,COMMUNITY_NAME,OBJECT_ID,YJJC_CREATE_TIME,YJJC_UPDATE_TIME from QHSJ_AIR_MONITOR_DAY_DATA where 1=1
and SITE_CODE = #{id}
and MONITOR_FACTOR_CODE = #{MONITOR_FACTOR_CODE}
and to_char(MONITOR_TIME,'yyyy-MM-dd HH24:mi:ss') like concat(#{DAY},'%')
order by MONITOR_TIME

SELECT AVG(t1.MONITOR_VALUE) AS VALUE, t1.MONITOR_FACTOR_CODE AS CODE, TO_CHAR(t1.MONITOR_TIME, 'yyyy-mm-dd') AS NAME FROM QHSJ_AIR_MONITOR_DAY_DATA t1 JOIN ( SELECT DISTINCT SITE_CODE FROM QHSJ_T_AIR_BASICINFO ) t2 ON t1.SITE_CODE = t2.SITE_CODE WHERE t1.MONITOR_FACTOR_CODE IS NOT NULl AND t1.MONITOR_VALUE IS NOT NULL
AND t1.MONITOR_TIME >= #{begin}
GROUP BY t1.MONITOR_FACTOR_CODE, TO_CHAR(t1.MONITOR_TIME, 'yyyy-mm-dd')

select  DATA_STATUS,ID,IS_REVISED,MN,MONITOR_FACTOR_CODE,MONITOR_TIME,MONITOR_VALUE,
REVISED_MONITOR_VALUE,SITE_CODE,STANDARD_VALUE,UPDATE_DATE,YXY_UPDATEDTIME,ADQ_UPDATE_TIME
from QHSJ_AIR_MONITOR_DAY_DATA
where 1=1

    and SITE_CODE = #{id}


    and MONITOR_FACTOR_CODE = #{MONITOR_FACTOR_CODE}


    and to_char(MONITOR_TIME,'yyyy-MM-dd HH24:mi:ss') = #{time}


    and MONITOR_TIME >= #{startTime}


    and MONITOR_TIME < #{endTime}

order by MONITOR_TIME
